import {
  registerRequestTransferHandler,
  RequestTransferParams,
} from "@nfid/wallet"
import { ActorRefFrom, assign, createMachine } from "xstate"

import { AuthSession } from "frontend/state/authentication"
import { AuthorizingAppMeta } from "frontend/state/authorization"
import AuthenticationMachine from "frontend/state/machines/authentication/authentication"

// State local to the machine.
interface Context {
  appMeta?: AuthorizingAppMeta
  authSession?: AuthSession
  requestTransfer?: RequestTransferParams
  amount?: number
  to?: string
  isLoading?: boolean
}

let credentialResolved = false

// Definition of events usable in the machine.
type Events =
  | {
      type: "done.invoke.AuthenticationMachine"
      data: AuthSession
    }
  | {
      type: "done.invoke.registerRequestTransferHandler"
      data: RequestTransferParams
    }
  | { type: "CONSENT" }
  | { type: "SUCCESS" }
  | { type: "REJECT" }
  | { type: "END" }

// The machine. Install xstate vscode extension for best results.
/** @xstate-layout N4IgpgJg5mDOIC5QAUAWB7AdmAcgVwFsAjMAJwGFTIxMAXASwEMAbZU9AN3ojIDoAlMIwgBPAMSJQAB3Sx6DLJJAAPRAEYAHGt4AWNQHYtAViMAGHRv2mATABoQI9UYCcO3mYDMpo-o--MSDxSvBFEb4ADUyPRYSIk+CStgyhVqrUOGmM1mAMpkLi5bpY3rozGgQZ+ZzuJ3DN1LbY+fSe6zMzZGSwWdlePwc0JcsXIq6xHIJe7JL6iiJT1Kp0jpzOTnliMjsVJSZgFWHoUgEXhF9clrdg8ukSvV6Ro-pYwZqHRuPx6czDWnmObDHsLDtd9TGcIx-QOPYNHDa9IxnW5YwXYVXizSVpVIUsWlLRUC0wMRyA1NIcCePVNR1PVDRNM0ayfS1rTWbRnC8XtJkdNRqSDDQe2GfR9D9SxrB8Ix2JDXwYOXHko1nAU40XVCpUKDCsJwmo8IVZU1Uw0tVUVLUAGkFXNOsrRfHFhM2fQ9m2HjaQHZYHCpZwf10LRTBHIxvV7aDxyzKSEPnIUExQ2C+AUsgtJUmgxH4b5SwVAARIznwbKkpnxBl-- */
const RequestTransferMachine = createMachine(
  {
    context: {} as Context,
    tsTypes: {} as import("./request-transfer.typegen").Typegen0,
    schema: { events: {} as Events },
    id: "PhoneNumberCredentialProvider",
    initial: "Ready",
    states: {
      Ready: {
        invoke: {
          src: "registerRequestTransferHandler",
          id: "registerRequestTransferHandler",
          onDone: {
            target: "Authenticate",
            actions: "assignRequestTransferRequest",
          },
        },
      },
      Authenticate: {
        invoke: {
          src: "AuthenticationMachine",
          id: "AuthenticationMachine",
          onDone: [{ target: "RequestTransfer", actions: "assignAuthSession" }],
          data: (context) => ({
            appMeta: context.appMeta,
          }),
        },
      },
      RequestTransfer: {
        on: {
          SUCCESS: "End",
        },
      },
      End: {
        type: "final",
      },
    },
  },
  {
    actions: {
      assignAuthSession: assign((_, event) => ({
        authSession: event.data,
      })),
      assignRequestTransferRequest: assign({
        requestTransfer: (_, event) => event.data,
      }),
    },
    services: {
      async registerRequestTransferHandler() {
        const params = await registerRequestTransferHandler(() => {
          return new Promise((resolve) => {
            setInterval(
              () =>
                credentialResolved && resolve({ status: "SUCCESS", height: 0 }),
              1000,
            )
          })
        })
        console.debug("registerRequestTransferHandler", { params })
        return params
      },
      AuthenticationMachine,
    },
    guards: {},
  },
)

export default RequestTransferMachine

export type RequestTransferMachineActor = ActorRefFrom<
  typeof RequestTransferMachine
>
export type RequestTransferMachineType = typeof RequestTransferMachine
